function currencySymbol(currency) {
    const symbols = {
        btc: "₿",
        usd: "$",
        eur: "€",
        uah: "₴"
    };
    return symbols[currency] || currency;
}

function renderChevrons() {
    const container = document.getElementById("chevrons-container");
    if (!container) return;

    container.innerHTML = "";
    window.chevrons.forEach(chevron => {
        const chevronDiv = document.createElement("div");
        chevronDiv.className = `chevron ${chevron.class}`;
        chevronDiv.innerHTML = `
            <img src="${chevron.image}" alt="${chevron.name}" class="chevron-preview">
            <h3>${chevron.name}</h3>
            ${chevron.costs ? `
                <p class="chevron-costs">
                    ${Object.entries(chevron.costs).map(([currency, amount]) => 
                        `${amount} ${currencySymbol(currency)}`
                    ).join(", ")}
                </p>
            ` : ""}
            <p class="chevron-class">${chevron.class}</p>
            ${chevron.exclusive ? '<p class="exclusive">Эксклюзив</p>' : ''}
            ${chevron.costs ? `
                <button class="chevron-buy-button" data-id="${chevron.id}">
                    Купить
                </button>
            ` : ""}
        `;
        container.appendChild(chevronDiv);
    });

    document.querySelectorAll(".chevron-buy-button").forEach(button => {
        button.addEventListener("click", (e) => {
            const chevronId = e.target.getAttribute("data-id");
            const chevron = window.chevrons.find(c => c.id === chevronId);
            if (chevron) {
                alert(`Вы купили шеврон ${chevron.name}!`);
            }
        });
    });
}

document.addEventListener("DOMContentLoaded", renderChevrons);